function calculateSum(a, b) {
  return a + b;
}

const result = calculateSum(5, 3);
console.log(result);

const numbers = [1, 2, 3, 4, 5];
const doubled = numbers.map(n => n * 2);
const sum = doubled.reduce((acc, n) => acc + n, 0);
console.log(sum);

def fibonacci(n):
    if n <= 1:
        return n
    return fibonacci(n-1) + fibonacci(n-2)

print(fibonacci(10))

interface User {
  id: number;
